targets.expr:
{job="blue_iris"} 
|~ "Started version|now available" 
| line_format "{{if contains \"now available\" __line__}}UPDATE_AVAILABLE{{else}}UP_TO_DATE{{end}}"

sum by (object, alert_type, ai_object) (last_over_time(
  {job="blue_iris", duration!="", object=~"$camera", ai_object=~"$object", alert_type=~"$type"} 
  | unwrap duration [15s]
))

avg by (object, alert_type, ai_object) (avg_over_time(
  {job="blue_iris", duration!="", object=~"$camera", ai_object=~"$object", alert_type=~"$type"} 
  | unwrap duration [$__range]
))

sum by (message) (
  count_over_time(
    {job="blue_iris", level="error"} | regexp "(?P<message>.*)" [$__range]
  )
) or on() vector(0)

sum (
  count_over_time(
    {job="blue_iris", level="error"} [$__range]
  )
) or on() vector(0)

sum by (message) (
  count_over_time(
    {job="blue_iris", level="warning"} | regexp "(?P<message>.*)" [$__range]
  )
) or on() vector(0)

sum (
  count_over_time(
    {job="blue_iris", level="warning"} [$__range]
  )
) or on() vector(0)
